mapper.select:
select
COALESCE(t.image_urls,'') as image_urls, COALESCE(t.image_ids,'') as image_ids from hxdb.hx_homepage_advertisement h left join ( select re.service_id,string_agg(img.image_url,',') as image_urls,string_agg(img.id,',') as image_ids from (select * from hxdb.hx_images_advertisement_rel order by sort asc) re left join hxdb.sys_images img on img.id = re.image_id group by re.service_id )t on t.service_id = h.id where del_flag = '0'
and homepage_name like '%'||#{homepageName}||'%'
and service_type = #{serviceType}
and h.status = #{status}

	select count(id) as totalcount from hxdb.hx_homepage_advertisement where del_flag = '0'


    and homepage_name like '%'||#{homepageName}||'%'



    and service_type = #{serviceType}



    and status = #{status}

mapper.update:
update  hxdb.hx_homepage_advertisement set del_flag = '1'
where id in

    #{item}

update  hxdb.hx_homepage_advertisement set status = #{status}
where id in

    #{item}
