mapper.select:
select COALESCE(a.city,'') as city,t.id as user_id, t.nick_name, t.phone,t.password,COALESCE(t.wallet_balance,0) as wallet_balance, COALESCE(t.wallet_pwd,'') as wallet_pwd,COALESCE(t.city_code,'') as city_code, hxdb.datestr_to_timestamp(to_char(t.register_time,'YYYY-MM-DD HH24:MI:SS')) as register_time, hxdb.datestr_to_timestamp(to_char(t.login_time,'YYYY-MM-DD HH24:MI:SS')) as login_time, COALESCE(t.login_num,0) as login_num,COALESCE(t.is_authentication,'') as is_authentication, COALESCE(t.sex,'') as sex,COALESCE(t.autograph,'') as autograph,COALESCE(t.credit,0) as credit, hxdb.datestr_to_timestamp(to_char(t.create_time,'YYYY-MM-DD HH24:MI:SS')) as create_time, hxdb.datestr_to_timestamp(to_char(t.update_time,'YYYY-MM-DD HH24:MI:SS')) as update_time, hxdb.datestr_to_timestamp(to_char(t.lock_time,'YYYY-MM-DD HH24:MI:SS')) as lock_time, t.lock_days,COALESCE(m.image1_url,'') as head_image_urls,t.del_flag, t.is_flag_nick_name,COALESCE(t.lock_reason,'') as lock_reason,COALESCE(t.is_recommend,'') as is_recommend,t.sort, CASE WHEN lock_date> NOW() THEN '0' ELSE '1' END as is_lock from ( SELECT u.*,n.lock_date from hxdb.hx_user u inner JOIN (SELECT id,lock_time + (lock_days || ' D')::"interval" as lock_date FROM hxdb.hx_user ) n on n.id=u.id) t LEFT JOIN hxdb.sys_area a on a.code=T.city_code left join hxdb.hx_images_user_rel y on y.service_id=t.id left join hxdb.sys_images m on y.image_id=m.id where 1=1
and t.id = #{userId}
and t.phone = #{phone}
and t.del_flag='0'

select COALESCE(a.city,'') as city,t.id as user_id, t.nick_name, t.phone,t.password,COALESCE(t.wallet_balance,0) as wallet_balance, COALESCE(t.wallet_pwd,'') as wallet_pwd,COALESCE(t.city_code,'') as city_code, hxdb.datestr_to_timestamp(to_char(t.register_time,'YYYY-MM-DD HH24:MI:SS')) as register_time, hxdb.datestr_to_timestamp(to_char(t.login_time,'YYYY-MM-DD HH24:MI:SS')) as login_time, COALESCE(t.login_num,0) as login_num,COALESCE(t.is_authentication,'') as is_authentication, COALESCE(t.sex,'') as sex,COALESCE(t.autograph,'') as autograph,COALESCE(t.credit,0) as credit, hxdb.datestr_to_timestamp(to_char(t.create_time,'YYYY-MM-DD HH24:MI:SS')) as create_time, hxdb.datestr_to_timestamp(to_char(t.update_time,'YYYY-MM-DD HH24:MI:SS')) as update_time, hxdb.datestr_to_timestamp(to_char(t.lock_time,'YYYY-MM-DD HH24:MI:SS')) as lock_time, hxdb.datestr_to_timestamp(to_char(now(),'YYYY-MM-DD HH24:MI:SS')) as current_time, t.lock_days,COALESCE(m.image1_url,'') as head_image_urls,t.del_flag, t.is_flag_nick_name,COALESCE(t.lock_reason,'') as lock_reason,COALESCE(t.is_recommend,'') as is_recommend,t.sort, CASE WHEN lock_date> NOW() THEN '0' ELSE '1' END as is_lock from ( SELECT u.*,n.lock_date from hxdb.hx_user u inner JOIN (SELECT id,lock_time + (lock_days || ' D')::"interval" as lock_date FROM hxdb.hx_user ) n on n.id=u.id) t LEFT JOIN hxdb.sys_area a on a.code=T.city_code left join hxdb.hx_images_user_rel y on y.service_id=t.id left join hxdb.sys_images m on y.image_id=m.id where 1=1
and t.phone = #{phone}
and t.password = #{password}
and t.del_flag='0'

mapper.update:
update hxdb.hx_user
wallet_balance = wallet_balance - #{money}, update_time=now()
where id=#{user_id}

mapper.select:
select
id,user_id,receive_address,receive_user,contact_phone,is_default,extract(epoch from to_timestamp(to_char(create_time,'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS')) create_time
from hxdb.hx_my_receive_address
where 1=1

	and id=#{addressId}


	and user_id=#{userId}

mapper.update:
update hxdb.hx_user
login_num = login_num+1, login_time=now()
WHERE phone=#{phone}
